function aliSharePlayUrl(share_id, file_id, alitoken) {
    try {
        function getNowTime() {
            const yy = new Date().getFullYear()
            const MM = (new Date().getMonth() + 1) < 10 ? '0' + (new Date().getMonth() + 1) : (new Date().getMonth() + 1)
            const dd = new Date().getDate() < 10 ? '0' + new Date().getDate() : new Date().getDate()
            const HH = new Date().getHours() < 10 ? '0' + new Date().getHours() : new Date().getHours()
            const mm = new Date().getMinutes() < 10 ? '0' + new Date().getMinutes() : new Date().getMinutes()
            return yy + '' + dd + '' + HH + '' + MM + '' + mm
        }

        let sharetoken = JSON.parse(request('https://api.aliyundrive.com/v2/share_link/get_share_token', {
            body: {
                "share_pwd": "",
                "share_id": share_id
            },
            method: 'POST',
            timeout: 3000
        })).share_token;

        let headers = {
            'content-type': 'application/json;charset=UTF-8',
            "origin": "https://www.aliyundrive.com",
            "referer": "https://www.aliyundrive.com/",
            //"user-agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/110.0.0.0 Safari/537.36",
            "user-agent": PC_UA,
            "x-canary": "client=web,app=adrive,version=v3.1.0"
        };
        let nowtime = Date.now();
        let oldtime = parseInt(getMyVar('userinfoChecktime', '0').replace('time', ''));
        let userinfo;
        let aliuserinfo = storage0.getMyVar('aliuserinfo');
        if (aliuserinfo && aliuserinfo.user_id && nowtime < (oldtime + 1 * 60 * 60 * 1000)) {
            userinfo = aliuserinfo;
        } else {
            userinfo = JSON.parse(request('https://auth.aliyundrive.com/v2/account/token', {
                headers: headers,
                body: {
                    "refresh_token": alitoken,
                    "grant_type": "refresh_token"
                },
                method: 'POST',
                timeout: 3000
            }));
            storage0.putMyVar('aliuserinfo', userinfo);
            putMyVar('userinfoChecktime', nowtime + 'time');
        }
        let authorization = 'Bearer ' + userinfo.access_token;
        let deviceId = userinfo.device_id;
        let userId = userinfo.user_id;
        let signature;

        let as = justTestSign("5dde4e1bdf9e4966b387ba58f4b3fdc3", deviceId, userId);
        //log(a);
        let bp = as.split("##");
        var sign = bp[0];
        var pubKey = bp[bp.length - 1];

        let i = 0;
        do {

            let sess = fetch('https://api.aliyundrive.com/users/v1/users/device/create_session', {
                headers: {
                    'Content-Type': 'application/json',
                    'User-Agent': PC_UA,
                    'Referer': 'https://www.aliyundrive.com/',
                    'Authorization': authorization,
                    'X-Share-Token': sharetoken,
                    'x-device-id': deviceId,
                    'x-signature': sign
                },
                body: '{"deviceName":"Chromeæµè§ˆå™¨","modelName":"Windowsç½‘é¡µç‰ˆ","nonce":0,"pubKey":"' + pubKey + '","refreshToken":"' + alitoken + '"}',
                method: 'POST'
            });
            if (sess.includes('success')) {
                break;
            }
            log('create_session ' + sess);
            as = justTestSign("5dde4e1bdf9e4966b387ba58f4b3fdc3", deviceId, userId);
            //log(a);
            bp = as.split("##");
            sign = bp[0];
            pubKey = bp[bp.length - 1];
            i++;
        } while (i < 5);

        signature = sign;
        headers['authorization'] = authorization;
        headers['x-device-id'] = deviceId;
        headers['x-signature'] = signature;
        let aliyunUrl = [];
        headers['x-share-token'] = sharetoken;
        headers['fileid'] = userId;
        let data = {
            "category": "live_transcoding",
            "file_id": file_id,
            "get_preview_url": true,
            "share_id": share_id,
            "template_id": "",
            "get_subtitle_info": true
        }
        //log(data);
        let json = JSON.parse(request('https://api.aliyundrive.com/v2/file/get_share_link_video_preview_play_info', {
            headers: headers,
            body: data,
            method: 'POST',
            timeout: 3000
        }));
        aliyunUrl = json.video_preview_play_info.live_transcoding_task_list;
        aliyunUrl.reverse();

        return aliyunUrl;
    } catch (e) {
        log('æ ¹æ®å…±äº«é“¾æ¥è·å–æ’­æ”¾åœ°å€å¤±è´¥>' + e.message);
        return "";
    }
}

function getAliUrl(share_id, file_id, alitoken) {
    try {
        let urls = [];
        let names = [];
        let heads = [];
        let u = startProxyServer($.toString((aliSharePlayUrl, file_id, share_id, alitoken) => {
            function geturl(fileid, line) {
                //é¢„åŠ è½½æ—¶ä¼šå˜file_id,æ‰€ä»¥tsè¿‡æœŸæ›´æ–°æ—¶è¿˜å–åŸæ¥çš„id
                let playUrlList = aliSharePlayUrl(share_id, fileid, alitoken) || [];
                let aliurl;
                playUrlList.forEach((item) => {
                    if (item.template_id == line) {
                        aliurl = item.url;
                        /*aliurl = JSON.parse(request(item.url, {
                            headers: {
                                'User-Agent': PC_UA,
                                'Referer': 'https://www.aliyundrive.com/'
                            },
                            onlyHeaders: true,
                            redirect: false,
                            timeout: 3000
                        })).headers.location[0];*/
                    }
                })
                //ä¸Šé¢æ˜¯è·å–é˜¿é‡Œçš„æ’­æ”¾åœ°å€
                //log("æˆ‘åœ¨ä»£ç†" + aliurl);
                let home = aliurl.split('media.m3u8')[0];
                let f = fetch(aliurl, {
                    headers: {
                        'User-Agent': PC_UA,
                        'Referer': 'https://www.aliyundrive.com/'
                    },
                    timeout: 3000
                }).split("\n");
                let ff = f.map(it => {
                    if (it.startsWith("media-")) {
                        return "/proxy?url=" + base64Encode(home + it);
                    }
                    return it;
                }).join("\n");
                //log('ufid-'+fileid);
                writeFile('hiker://files/cache/_fileSelect_' + fileid + '.m3u8', ff);
                return ff;
            }
            let url = base64Decode(MY_PARAMS.url);
            if (url.includes(".ts")) {
                let fid = url.split('&f=')[1].split('&')[0];
                //log('sfid-'+fid);
                let f = fetch('hiker://files/cache/_fileSelect_' + fid + '.m3u8').split("\n");
                f.forEach(it => {
                    if (it && it.startsWith('/proxy?url=')) {
                        let furl = base64Decode(it.replace('/proxy?url=', ''));
                        if (url.substr(url.indexOf('/media-'), url.indexOf('.ts')) == furl.substr(furl.indexOf('/media-'), furl.indexOf('.ts'))) {
                            url = furl;
                        }
                    }
                })
                let expires = url.split('x-oss-expires=')[1].split('&')[0];
                const lasttime = parseInt(expires) - Date.now() / 1000;
                if (lasttime < 60) {
                    //log('è¿‡æœŸæ›´æ–°')
                    let line = url.split('/media')[0]; //å–ä¹‹å‰æ’­æ”¾çš„tsæ®µçº¿è·¯
                    line = line.substring(line.lastIndexOf('/') + 1);
                    let f = geturl(fid, line).split("\n");
                    f.forEach(it => {
                        if (it && it.startsWith('/proxy?url=')) {
                            let furl = base64Decode(it.replace('/proxy?url=', ''));
                            if (url.substr(url.indexOf('/media-'), url.indexOf('.ts')) == furl.substr(furl.indexOf('/media-'), furl.indexOf('.ts'))) {
                                url = furl;
                            }
                        }
                    })

                } else {
                    //log('æœªè¿‡æœŸ')
                    //log("ä»£ç†tsï¼š" + url);
                }
                return JSON.stringify({
                    statusCode: 302,
                    headers: {
                        "Location": url,
                        "User-Agent": PC_UA,
                        'Referer': 'https://www.aliyundrive.com/'
                    }
                });
            } else {
                //log('é¦–æ¬¡æ›´æ–°')
                let line = url.split('|')[1];
                let ff = geturl(file_id, line);
                return ff;
            }
        }, aliSharePlayUrl, file_id, share_id, alitoken));

        let playUrlList = aliSharePlayUrl(share_id, file_id, alitoken) || [];

        if (playUrlList.length > 0) {
            playUrlList.forEach((item) => {
                urls.push(u + "?url=" + base64Encode(item.url + "|" + item.template_id) + "#.m3u8");
                names.push(transcoding[item.template_id] ? transcoding[item.template_id] : item.template_height);
                heads.push({
                    'User-Agent': PC_UA,
                    'Referer': 'https://www.aliyundrive.com/'
                });
            })
            var mult = {
                urls: urls,
                names: names,
                headers: heads
            };
            if (zimu.length > 0) {
                for (let i in zimu) {
                    var zimut = zimu[i].tit;
                    if (fname.includes(zimut) || zimut.includes(fname.substring(0, fname.lastIndexOf('.')))) {
                        mult['subtitle'] = getsub(zimu[i].fid, zimu[i].ext);
                        break;
                    }
                }
            }

            return mult;
        } else {
            log('æœªè·å–é˜¿é‡Œæ’­æ”¾åœ°å€ï¼Œå»ºè®®é‡è¿›è½¯ä»¶å†è¯•ä¸€æ¬¡')
            return {};
        }
    } catch (e) {
        log('è·å–å…±äº«é“¾æ¥æ’­æ”¾åœ°å€å¤±è´¥>' + e.message);
        return {};
    }
}

