    search: (rule, kw, page) => {
        let d = [];
        let m = rule.ruleSearch
        // é˜…è¯»=>è§†ç•Œ
        let u = rule.bookSourceUrl + rule.searchUrl;
        let url = u.replace("{{page}}", page).replace("{{key}}", kw)
        let listrule = "body&&" + m.bookList.replace(/![0-9]/g, "").replace(" ", "&&").split("||")[0]
        let author = m.author.replace("@", "&&").replace("text", "Text").replace(/\.[0-9]/g, function(a) {
            return a.replace(".", ",")
        })
        let cover = m.coverUrl.replace("@", "&&").replace(/\.[0-9]/g, function(a) {
            return a.replace(".", ",")
        })
        let name = m.name.replace("@", "&&").replace("text", "Text").replace(/\.[0-9]/g, function(a) {
            return a.replace(".", ",")
        })
        let bookurl = m.bookUrl.replace("@", "&&").replace(/\.[0-9]/g, function(a) {
            return a.replace(".", ",")
        })
        let intro = m.intro.replace("@", "&&").replace("text", "Text").replace(/\.[0-9]/g, function(a) {
            return a.replace(".", ",")
        });
        //setPageTitle("æœç´¢" + getParam("kw") + " " + MY_PARAMS.bookSourceName);
        //MY_URL = url;
        let res = request(url)
        let list = pdfa(res, listrule)
        list.forEach(item => {
            d.push({
                title: parseDomForHtml(item, name),
                img: parseDom(item, cover, url),
                url: "hiker://page/menu?url=" + parseDom(item, bookurl, url) + "#autoCache##immersiveTheme#",
                desc: rule.bookSourceName + "\n" + parseDomForHtml(item, author) + "\n" + parseDomForHtml(item, intro),
                extra: {
                    "Toc": rule.ruleToc,
                    "content": rule.ruleContent,
                    "bookName": parseDomForHtml(item, name),
                    "cover": parseDom(item, cover, url)
                }
            })
        });
        return d;
    },
    listRules: (page, rule) => {
        try {
            //å¹¶å‘æ•°é‡
            let size = 20;
            let data1 = JSON.parse(fetch("hiker://files/yuedu/list.json"))
            let list = JSON.parse(fetch("hiker://files/yuedu/" + md5(getMyVar("tab", data1.urls[0].name)) + ".json"))
            let rules = [];
            list.forEach(item => {
                let s = JSON.stringify(item)
                if (s.includes("<js>") || s.includes("@js:") || s.includes("$.") || s.includes("ğŸ”")) {
                    return
                } else {
                    rules.push(item);
                }
            });
            if (!page && !rule) {
                return rules.map(it => it.name);
            }
            if (rule != null) {
                return rules.filter(i => i.name == rule);
            }
            let start = (page - 1) * size;
            if (rules.length < start + 1) {
                return [];
            }
            let len = size;
            if (rules.length < page * size) {
                len = rules.length - start
            }
            let all = rules.length;
            let use = (page - 1) * size + len;
            return {
                all: all,
                use: use,
                data: rules.slice(start, start + len)
            };
        } catch (e) {
            return {
                all: 0,
                use: 0,
                data: []
            }
        }
    }
}"}]

