    bytesToSize(size) {
        if (!size) {
            return '0';
        }
        const units = ['B', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];
        let i = 0;
        while (size >= 1024) {
            size /= 1024;
            i++;
        }
        size = i ? Number(size.toFixed(2)) : size;
        return `${size} ${units[i]}`;
    },
    getCryptoJS() {
        if (typeof CryptoJS === "undefined") {
            eval(getCryptoJS());
            return CryptoJS;
        }
        return CryptoJS;
