$.exports.getFavourIdList = function (aid, userMid) {
    let u = "https://api.bilibili.com/x/v3/fav/folder/created/list-all";
    let res = request(buildUrl(u, {
        "type": 2,
        "rid": aid,
        "up_mid": userMid,
    }), {
        headers: {
            "Cookie": JSON.parse(request("hiker://files/rules/cy/bili.txt")).cookie,
            "Referer": "https://www.bilibili.com"
        }
    });
    favourIdList = [];
    JSON.parse(res).data.list.forEach((item) => {
        if (item.fav_state == 1) {
            favourIdList.push(item.id);
        }
    })
    return favourIdList;
}
