 * é“é•¿ä»“åº“é“¾æ¥ http://hiker.nokia.press/hikerule/rulelist.json?id=2849
 * ç äº‘ Gitee é“¾æ¥ https://gitee.com/reborn0/HikerRules/raw/master/plugins/UrlProcessor.js
 */
function UrlProcessor() {
    this.params = {}
    this.regexMap = [
        {
            reg: '(vodtype|videot)\\/\\w+',
            exec: () => {
                return this.params.url
                    .replace(/-\d+.html/, '-' + this.params.page + '.html')
                    .replace(/((vodtype|videot)\/\w+).html/, '$1-' + this.params.page + '.html')
            }
        },
        {
            reg: '\\/?[\\w\\d]+-.*?-.*?-.*?-.*?-.*?-.*?-.*?-\\d*---\\d*.html',
            exec: () => {
                return this.params.url
                    .replace(/\d*(---.html)/, this.params.page + '$1')
                    .replace(/(---\d+.html)/, this.params.page + '$1'); // æ‹¼æ¥é¡µç 
            }
        },
        {
            reg: '((\\/vod\\/show)?(\\/area\\/[\\w\\d%]+)?(\\/by\\/[\\w\\d%]+)?(\\/id\\/\\d+)?(\\/letter\\/[\\w\\d%]+)?)(\\/page\\/\\d+)?(\\/year\\/\\d+)?\\.html',
            exec: () => {
                let regExp = /((\/vod\/show)?(\/area\/[\w\d%]+)?(\/by\/[\w\d%]+)?(\/id\/\d+)?(\/letter\/[\w\d%]+)?)(\/page\/\d+)?(\/year\/\d+)?\.html/
                return this.params.url
                    .replace(regExp, '$1' + ("/page/" + this.params.page) + '$8' + ".html")
            }
        }
    ]
}

Object.assign(UrlProcessor.prototype, {
    constructor: UrlProcessor,
    checkParams() {
        if (!this.params.url) {
            throw new Error("è¯·å…ˆè°ƒç”¨ .baseUrl(url) ä¼ å…¥é“¾æ¥è¿›è¡Œåˆå§‹åŒ–!")
        }
        if (!this.params.page) {
            throw new Error("è¯·å…ˆè°ƒç”¨ .page(page) ä¼ å…¥é¡µç è¿›è¡Œåˆå§‹åŒ–!")
        }
    },
    baseUrl(url){
        this.params.url = url
        return this
    },
    page(page){
        this.params.page = page
        return this
    },
    addExtra(regExp) {
        if (regExp.constructor === Array) {
            this.regexMap = this.regexMap.concat(regExp)
        } else {
            this.regexMap.push(regExp)
        }
        return this;
    },
    debug() {
        this.params.debug = true
        return this;
    },
    exec() {
        this.checkParams()
        let true_url = ''
        for (let i = 0; i < this.regexMap.length; i++) {
            let regObj = this.regexMap[i];
            let urlExp = regObj.reg;
            if (typeof urlExp === 'string') {
                urlExp = new RegExp(urlExp)
            }
            if (urlExp.test(this.params.url)) {
                if (this.params.debug) {
                    log(urlExp.toString())
                }
                true_url = regObj.exec();
                break;
            }
        }
        if (true_url) {
            return true_url;
        } else {
            return this.params.url
        }
    },
    è·å–å¤„ç†ç»“æœ() {
        return this.exec()
    },
    é“¾æ¥(url) {
        return this.baseUrl(url)
    },
    é¡µç (page) {
        return this.page(page)
    },
    æ’å…¥æ–°å¤„ç†è§„åˆ™(regExp) {
        return this.addExtra(regExp)
    },
    è°ƒè¯•æ¨¡å¼() {
        return this.debug();
    }
})
