eval(JSON.parse(request("hiker://page/config")).rule); //getConfig

let data = [],
    config = getConfig();
config.data = config.data.filter(v => !v.inVisible);

if (config.data.length <= 0) {
    data = $.require('hiker://page/renderSetting').renderSetting();
} else {
    let select = getItem("live-select", config.data[0].name),
        has = config.data.map(i => i.name).includes(select);
    if (!has) {
        select = config.data[0].name;
    }

    data.push({
        title: '聚直播'.bold() + ' &nbsp &nbsp ' + '⚙ 聚直播设置 ⚙'.small(),
        col_type: "avatar",
        pic_url: MY_RULE.icon,
        url: $("hiker://empty#noLoading#").rule(() => {
            addListener('onClose', 'refreshPage()')
            setResult($.require('hiker://page/renderSetting').renderSetting())
        })
    })

    data.push({
        title: '搜索',
        desc: '请输入搜索关键词',
        extra: {
            defaultValue: getVar('search_key', ''),
            onChange: 'putVar("search_key",input)'
        },
        col_type: 'input',
        url: $.toString(() => {
            return 'hiker://search?rule=' + getMyVar('title') + '&s=' + getVar('search_key', '')
        })
    })



    for (source of config.data) {
        data.push({
            title: source.name == select ? "““””<span style='color: #19B89D'>" + select + "</span>" : source.name,
            col_type: "scroll_button",
            url: $('#noLoading#').lazyRule((a) => {
                setItem("live-select", a);
                setItem('selectGroup', '');
                refreshPage();
                return "hiker://empty"
            }, source.name)
        })
    }

    data.push({
        col_type: 'blank_block'
    })

    if (typeof(setPreResult) != "undefined") {
        data.push({
            pic_url: "https://hikerfans.com/weisyr/img/Loading1.gif",
            col_type: "pic_1_center",
            extra: {
                cls: "loading_gif"
            }
       })
       setPreResult(data);
       data = [];
    }

    for (source of config.data) {
        if (source.name == select) {
            try {
                let items = $.require('hiker://page/renderData').renderLiveData(source.url,source.ua);
                let groups = Object.keys(items);
                //groups.push(groups.splice(groups.indexOf(0), 1)[0]);
                let selectGroup = getItem('selectGroup', groups[0]);
                if (selectGroup == "" || !groups.includes(selectGroup)) selectGroup = groups[0];
                groups.forEach((group) => {
                    if (group) {
                        data.push({
                            title: group == selectGroup ? "““””<span style='color: #19B89D'>" + group + "</span>" : group,
                            url: $('#noLoading#').lazyRule((group) => {
                                setItem('selectGroup', group);
                                refreshPage();
                                return 'hiker://empty'
                            }, group),
                            col_type: 'scroll_button',
                            extra: {
                                lineVisible: false
                            }
                        })
                    }
                })

                for (let title in items[selectGroup]) {
                    data.push({
                        title: title,
                        url: $().lazyRule((sourceurl,selectGroup,title) => {
                            let items = $.require('hiker://page/renderData').renderLiveData(sourceurl);
                            let surls = items[selectGroup][title].url;
                            let sheaders = items[selectGroup][title].headers;
                            for (let groupi in items){
                                let group = items[groupi];
                                if(!group) continue;
                                for (let ititle in group){
                                    if(ititle == title && !!group[ititle]){
                                        let urls = group[ititle].url;
                                        let headers = group[ititle].headers;
                                       surls = surls.concat(urls);
                                       sheaders = sheaders.concat(headers);
                                    }
                                }
                            }
                            surls = Array.from(new Set(surls));
                            sheaders = Array.from(new Set(sheaders));
                            return JSON.stringify({
                                urls: surls,
                                headers: sheaders
                            })
                        },source.url,selectGroup,title),
                        //col_type: 'text_3',
                        img: "https://hikerfans.com/tubiao/more/228.png"
                    })
                }
            } catch (e) {
                data.push({
                    title: "数据加载失败\n" + JSON.stringify(e),
                    col_type: "long_text"
                })
            }
            break;
        }
    }
    deleteItemByCls("loading_gif");
}
setResult(data);

