js:
eval(JSON.parse(request("hiker://page/config")).rule);
let config = getConfig();
let rules = config.data.map((source) => {
    return {
        title: source.name,
        search_url: 'hiker://empty?key=**',
        searchFind: 'js:' + $.toString((url, rule_title) => {
            let d = [],
                key = getParam('key');
            let items = $.require('hiker://page/renderData?rule=' + rule_title).renderLiveData(url);
            for (let group in items) {
                for (let title in items[group]) {
                    if (title.indexOf(key) >= 0 || title.toLowerCase().indexOf(key) >= 0) {
                        d.push({
                            title: title,
                            url: JSON.stringify({
                                urls: items[group][title].url,
                                headers: items[group][title].headers
                            })
                        })
                    }
                }
            }
            setResult(d);
        }, source.url, getMyVar('title'))
    };
})
setResult([{
    title: "点我开始聚搜",
    url: "hiker://search?s=" + getParam("key"),
    extra: {
        rules: $.toString((rules) => {
            return JSON.stringify(rules)
        }, rules)
    }
}])

