    ['启用禁用', '删除订阅', '重新排序', '编辑订阅', '清除缓存'].forEach((v) => {
        d.push({
            title: v == editMode ? '““””' + v.bold().fontcolor('#19B89D') : v,
            url: v == editMode ? 'hiker://empty' : $('#noLoading#').lazyRule((v) => {
                putMyVar('editMode', v)
                refreshPage(false)
                return 'hiker://empty'
            }, v),
            col_type: 'scroll_button'
        })
    });
    config.data.forEach((v, i) => {
        let el = {
            title: v.name,
            col_type: "text_3"
        }
        switch (editMode) {
            case '启用禁用':
                el.title = (v.inVisible ? '🔴 ' : '🟢 ') + el.title
                el.url = $('#noLoading#').lazyRule((i) => {
                    eval(JSON.parse(request("hiker://page/config")).rule);
                    config = getConfig();
                    config.data[i].inVisible = !config.data[i].inVisible;
                    writeFile("hiker://files/rules/live/config.json", JSON.stringify(config));
                    refreshPage(false);
                    return 'hiker://empty'
                }, i)
                break
            case '删除订阅':
                el.url = "confirm://确认删除“" + v.name + "”订阅？.js:" + $.toString((i) => {
                    eval(JSON.parse(request("hiker://page/config")).rule);
                    config = getConfig();
                    config.data.splice(i, 1);
                    writeFile("hiker://files/rules/live/config.json", JSON.stringify(config));
                    refreshPage(false);
                    return "toast://已删除";
                }, i)
                break
            case '重新排序':
                let sortFlag = parseInt(getMyVar('sortFlag', '-1'))
                el.title = (sortFlag == i ? '🔃  ' : '') + el.title
                if (sortFlag == -1)
                    el.url = $('#noLoading#').lazyRule((i) => {
                        putMyVar('sortFlag', i.toString())
                        refreshPage(false)
                        return 'toast://选择要移动到的位置'
                    }, i)
                else
                    el.url = $('#noLoading#').lazyRule((oldIndex, newIndex) => {
                        eval(JSON.parse(request("hiker://page/config")).rule);
                        config = getConfig();
                        config.data.splice(newIndex, 0, config.data.splice(oldIndex, 1)[0])
                        writeFile("hiker://files/rules/live/config.json", JSON.stringify(config));
                        putMyVar('sortFlag', '-1')
                        refreshPage(false)
                        return 'hiker://empty'
                    }, sortFlag, i)
                break
            case '编辑订阅':
                let arr = ['编辑名称', '编辑地址', '编辑内容'];
                el.url = $(arr, 3).select((v, i) => {
                    if (input == '编辑名称') {
                        return $(v.name, '输入新名称').input((i) => {
                            eval(JSON.parse(request("hiker://page/config")).rule);
                            config = getConfig();
                            if (input) {
                                config.data[i].name = input
                                writeFile("hiker://files/rules/live/config.json", JSON.stringify(config));
                                refreshPage(false)
                                return 'toast://修改成功'
                            } else {
                                return 'toast://不能为空'
                            }
                        }, i);
                    } else if (input == '编辑地址') {
                        return $(v.ua ? v.url + '$$$' + v.ua : v.url, '输入新地址').input((i) => {
                            eval(JSON.parse(request("hiker://page/config")).rule);
                            config = getConfig();
                            if (input == "") {
                                return "toast://不能为空"
                            } else if (input.indexOf("http") != 0 && input.indexOf("file") != 0 && input.indexOf("hiker") != 0) {
                                return "toast://链接格式有误"
                            } else {
                                let [url,ua] = input.split('$$$');
                                config.data[i].url = url;
                                config.data[i].ua = ua;
                                writeFile("hiker://files/rules/live/config.json", JSON.stringify(config));
                                refreshPage(false)
                                return 'toast://修改成功'
                            }
                        }, i);
                    } else if (input == '编辑内容') {
                        if (v.url.startsWith('file://') || v.url.startsWith('hiker://files')) {
                            return 'editFile://' + v.url;
                        }
                        return 'editFile://hiker://files/libs/' + md5(v.url) + '.js'
                    }
                }, v, i);
                break;
            case '清除缓存':
                el.url = $('确定清除“' + v.name + '”订阅缓存？\n(可立即更新)').confirm((url) => {
                    deleteCache(url);
                    return 'toast://清除成功'
                }, v.url)
                break;
        }
        d.push(el)
    });
