}"},{"col_type":"movie_3","name":"批量导入","path":"batchIm","rule":"function batchImport(input, rule) {
    input = input.trim();
    if (input.length < 3 || input[0] != "{") {
        return "toast://请输入正确的格式"
    }
    let im = JSON.parse(input);
    if (im.data == null || im.data.length < 1) {
        return "toast://请输入正确的格式"
    }
    eval(JSON.parse(request("hiker://page/config?rule=" + rule)).rule);
    let config = getConfig();
    let count = 0;
    let all = config.data.map(it => it.url + "&&&").join();
    for (let i of im.data) {
        if (all.indexOf(i.url + "&&&") >= 0) {
            //重复的不要
            continue;
        }
        count++;
        config.data.push(i)
    }
    writeFile("hiker://files/rules/live/config.json", JSON.stringify(config));
    refreshPage();
    return "toast://已导入" + count + "条订阅源";
}"},{"col_type":"movie_3","name":"直播数据","path":"renderData","rule":"function renderLiveData(url) {
    let m3u = "";
    if (url.startsWith('hiker://') || url.startsWith('file://')) {
        m3u = fetch(url);
    } else {
        let interval = Number(getItem('interval', '24')),
            oldData = fetch('hiker://files/libs/' + md5(url) + '.js'),
            remoteData = '';
        try {
            remoteData = fetchCache(url, interval, {
                timeout: 50000
            });
        } catch (e) {}
        if (!remoteData.includes('http') || remoteData.includes('<body>')) { 
            log('更新失败');
            if (oldData) writeFile('hiker://files/libs/' + md5(url) + '.js', oldData);
            m3u = oldData;
        } else {
            m3u = remoteData;
        }
    }

    let mode =  m3u.indexOf('#EXTM3U') == 0 , re;
    if(mode){
        re = /(.*),(.*)\s*?((?:http|rtmp)[^#\s]*)/g;
    }else{
        re = /()(?:\B\$c_start|\B#{1,2})?(.*?),?(##|头#|\$c_end|,?#genre#|(?:http|rtmp)[^\s]*)/g;
    }

    let items = {},
        group = '未分组';
    for (let i = re.exec(m3u); i != null; i = re.exec(m3u)) {
        if (mode) {
            group = (i[1].match(/group-title="(.*?)"/) || [null, group])[1];
        } else if (['#genre#','##','头#','$c_end'].includes(i[3])) {
            group = i[2];
            continue;
        }
        let title = i[2],
            url = i[3].includes('#') ? i[3].split('#') : [i[3]],
            headers = [];
        if(!url[url.length-1]) url.pop();
        url.forEach((v,index,array)=>{
            if(/;\{(.*)\}/.test(v)){
                let obj = {},
                     con = v.match(/;\{(.*)\}/)[1],
                     arr = con.split('&&');
                arr.forEach((i)=>{
                    let [key,value] = i.split('@');
                    obj[key] = value.replace(/;/g,'；；');
                })
                array[index] = v.match(/^([^;]*);\{/)[1]
                headers.push(obj);
            }else{
                headers.push({'User-Agent' : MOBILE_UA.replace(/;/g,'；；')});
            }
            array[index] = 'video://' + array[index].replace(/;/g,'；；');
        });
        if (items[group]) {
            if (items[group][title]) {
                items[group][title].url = items[group][title].url.concat(url);
                items[group][title].headers = items[group][title].headers.concat(headers);
            } else {
                items[group][title] = {url:url,headers:headers};
            }
        } else {
            items[group] = {};
            items[group][title] = {url:url,headers:headers};
        };
    }
    return items;
}

$.exports = {
    renderLiveData: renderLiveData
}
