$.exports.range = function(m, n) {
    return Math.floor(Math.random() * (m - n + 1) + n);
}
$.exports.mapColType = function(target, type, n) {
    for (let i = 0; i < n; i++) {
        target.push({
            col_type: type
        });
    }
}
var getApi = function(k) {
