$.exports.localRuleVer = function(localRuleList, rule) {
    let localRule;
    for (let i = 0; i < localRuleList.length; i++) {
        let item = localRuleList[i];
        let au = item.author || "";
        let sameAu=au.length > 0 && rule.author.includes(au);
        sameAu=true;//不判断作者默认为true
        if (item.title === rule.name && sameAu) {
            localRule = item;
            break;
        }
    }
    if (localRule === void 0) {
        return -1;
    } else {
        return localRule.version;
    }
}
$.exports.color = function(text, color) {
    text += "";
    if (text.indexOf("““””") === 0) {
        text.replace("““””", "");
    }
    return "““””<font color='" + color + "'>" + text + "</font>";
}

$.exports.sleep = function(timeout){
