                putVar((getVar("ClassicHeader.id", "classic") + "_cur_index"), key);
                refreshPage(false);
                return "hiker://empty"
            }, key),
            col_type: "scroll_button"
        });
    }
    d.push({
        col_type: "blank_block"
    });
    let key = getVar(getVar("ClassicHeader.id", "classic"), Object.keys(obj)[0]);
    putVar((getVar("ClassicHeader.id", "classic") + "_cur_index"), key);
    getNextHeaderFromJSONArray(key, obj[key]);
    return d;
}

function getNextHeaderFromJSONArray(key, array) {
    if (array == undefined || array.length == 0) {
        return;
    }
    let nextHeader = {};
    let defaultValue = "";
    defaultValue = array[0];
    if (Object.prototype.toString.call(array[0]) === '[object Object]') {
        defaultValue = Object.keys(array[0])[0];
    }
    putKeyIntoMap((getVar("ClassicHeader.id", "classic") + "@@" + key), getVar((getVar("ClassicHeader.id", "classic") + "@@" + key), defaultValue))
