            },
            set: function(newValue) {
                putVar(this.key, newValue);
            }
        },
        encode: {
            key: "crypto_output_encode",
            get: function() {
                return getVar(this.key, Crypto.ConfigMapping.encode["Base64"])
            },
            set: function(newValue) {
                putVar(this.key, newValue);
            }
        },
        key: {
            key: "crypto_key",
            get: function() {
                return getVar(this.key, "");
            },
