    },
    buildConfig: function() {
        let mode = this.config.mode.get();
        let pad = this.config.pad.get();
        let iv = this.config.iv.get();
        let conf = {};
        conf.mode = this.ConfigMapping.mode[mode];
        conf.padding = this.ConfigMapping.pad[pad]
        if (mode != "ECB" && iv.length != 0) {
            conf.iv = CryptoJS.enc.Utf8.parse(iv);
            if (this.config.ivEncodeType.get() == "Hex") {
                iv = iv.replace("[HEX]:", "")
                conf.iv = CryptoJS.enc.Hex.parse(iv);
            }
        }
        return conf
    },
    encryptor: function(obj) {
        // 原文
        let text = obj.decText;
        if (text) {
            let keyString = this.config.key.get()
            let key = CryptoJS.enc.Utf8.parse(keyString);
            if (this.config.keyEncodeType.get() == "Hex") {
                keyString = keyString.replace("[HEX]:", "")
                key = CryptoJS.enc.Hex.parse(keyString);
            }
            try {
                let conf = this.buildConfig()
                let encrypted = this.ConfigMapping.encType[this.config.type.get()](text, key, conf);
                let encode = this.config.encode.get()
                let cipherText = encrypted.toString();
                if (encode === 'HEX') {
                    cipherText = CryptoJS.enc.Hex.stringify(CryptoJS.enc.Base64.parse(cipherText));
                }
                // 密文
                this.config.cipherText.set(cipherText)
                return cipherText;
            } catch (e) {
