        } catch (e) {}
        if (type === "DES") {
            blockSize = 64;
        }
        if (Crypto.config.keyEncodeType.get() == "Hex") {
            key = key.replace("[HEX]:", "")
            blockSize = blockSize * 2;
        }
        // 设置数据块长度
        let keySize = key.length * 8;
        // log("checkKey#keySize:" + keySize)
        // log("checkKey#keySize/8:" + keySize/8)
        // log("checkKey#blockSize:" + blockSize)
        // log("checkKey#blockSize/8:" + blockSize / 8)
        if (keySize < blockSize) {
            obj.from = Crypto.config.keyEncodeType.key
            let newKey = Crypto.fillKeys(obj, key, blockSize / 8);
            Crypto.config.key.set(newKey)
            // log("checkKey#newKey: " + newKey)
            // log("checkKey#newKey.Size: " + newKey.length * 8)
            throw new Error('密钥 key 长度过短，已自动 `\\x00` 空填充，请再次执行加/解密')
        } else if (keySize > blockSize) {
