                obj.tools["AES"] = Crypto.AES;
                obj.tools["DES"] = Crypto.DES;
                obj.tools["3DES"] = Crypto["3DES"];
                var tool = {};
                for (let key in obj.tools) {
                    if (obj.type.includes("3DES")) {
                        tool = obj.tools["3DES"];
                        break;
                    }
                    if (obj.type.includes(key) && !obj.type.includes("3DES")) {
                        tool = obj.tools[key];
                        break;
                    }
                }
                cipherText = tool.encrypt(obj);
                // log("cipherText: " + cipherText)
