eval(getCryptoJS());
let HashAlgorithm = {
	Mapping: {
		hashType: {
			"MD5": CryptoJS.MD5,
			"SHA-1": CryptoJS.SHA1,
			"SHA-224": CryptoJS.SHA224,
			"SHA-256": CryptoJS.SHA256,
			"SHA-384": CryptoJS.SHA384,
			"SHA-512": CryptoJS.SHA512,
			"HMAC-MD5": CryptoJS.HmacMD5,
			"HMAC-SHA1": CryptoJS.HmacSHA1,
			"HMAC-SHA224": CryptoJS.HmacSHA224, 
			"HMAC-SHA256": CryptoJS.HmacSHA256, 
			"HMAC-SHA384": CryptoJS.HmacSHA384, 
			"HMAC-SHA512": CryptoJS.HmacSHA512,
			"PBKDF2": CryptoJS.PBKDF2,
		},
        keySize: {
            "128位": 128,
            "256位": 256,
            "512位": 512,
        },
	},
	config: {
        type: {
            key: "hash_type",
            get: function () {
                return getVar(this.key, "MD5");
            },
            set: function (newValue) {
                putVar(this.key, newValue);
            }
        },
        key: {
            key: "hash_hmac_key",
            get: function () {
                return getVar(this.key, "");
            },
            set: function (newValue) {
                putVar(this.key, newValue);
            }
        },
		text: {
			key: "hash_text",
            get: function () {
                return getVar(this.key, "");
            },
            set: function (newValue) {
                putVar(this.key, newValue);
            }
		},
		resultText: {
			key: "hash_result_text",
            get: function () {
                return getVar(this.key, "");
            },
            set: function (newValue) {
                putVar(this.key, newValue);
            }
		},
        keySize: {
            key: "hash_key_size",
            get: function () {
                return getVar(this.key, "128位");
            },
            set: function (newValue) {
                putVar(this.key, newValue);
            }

        },
        salt: {
            key: "hash_salt",
            get: function () {
                return getVar(this.key, "");
            },
            set: function (newValue) {
                putVar(this.key, newValue);
            }

        },
        iterations: {
            key: "hash_iterations",
            get: function () {
                return getVar(this.key, "");
            },
            set: function (newValue) {
                putVar(this.key, newValue);
            }

        },
	},
    onClose: function() {
        let configKeys = Object.keys(this.config);
        for(let confKey of configKeys) {
            let key = this.config[confKey].key;
            clearVar(key);
        }
    },
	hash: function () {
		let text = this.config.text.get();
		let type = this.config.type.get();
		let hash = {};
		if (text) {
            if (type.includes("HMAC")) {
                // HMAC-xxx
                let key = this.config.key.get();
                hash = this.Mapping.hashType[type](text, key)
            } else if (type.includes("PBKDF2")) {
                // PBKDF2
                let keySize = this.Mapping.keySize[this.config.keySize.get()];
                let salt = this.config.salt.get();
                let iterations = this.config.iterations.get();
                if (!salt) {
                	throw new Error("盐 salt 不能为空！")
                }
                salt = CryptoJS.enc.Utf8.parse(salt)
                let options =  {
                		keySize: keySize / 32
                }
                if (iterations) {
                	options.iterations = iterations;
                }
                hash = this.Mapping.hashType[type](text, salt, options)
            } else {
				hash = this.Mapping.hashType[type](text);
            }
			// 密文
			let hashResult = hash.toString();
            // log("hash#hash_result: " + hashResult)
            this.config.resultText.set(hashResult);
			return hashResult;
		} else {
            throw new Error("原文不能为空！")
        }
	}
};
