function fileRule(filesInput, fileOut, intercept) {
    with(javaScope) {
        const BUFFER_SIZE = 0x300000;
        let tmpFile = new File(filesInput);
        if (!(tmpFile.exists() && tmpFile.isFile())) {
            return false;
        }
        let outFile = new File(fileOut);

        let tis = new FileInputStream(tmpFile);
