    split(text, target, count) {
        count = count === undefined ? 1 : count;
        let array = [text];
        for (let i = 0; i < count; i++) {
            text = array[i];
            let index = text.indexOf(target);
            if (index === -1) {
                break;
            }
            array[i] = text.slice(0, index);
            array[i + 1] = text.slice(index + target.length);
        }
        return array;
    },
    chunk(arr, size) {
        var result = [];
        for (var i = 0, len = arr.length; i < len; i += size) {
            result.push(arr.slice(i, i + size));
        }
        return result;
    },
    hikerPic(url) {
        var headers = {};
        let urls = url.split("@");
        let picUrl = urls.shift();
        let length = urls.length;
        for (let i = 0; i < length; i++) {
            let [key, value] = v.split(urls[i], "=");
            if (key === "headers") {
                try {
                    Object.assign(headers, JSON.parse(value));
                } catch (e) {
                    log(e.toString());
                }
            } else if (key.includes(".") || key.includes("?")) {
                picUrl += "@" + key + (value ? "=" + value : "");
            } else if (key !== "js") {
                headers[key] = value;
            }
        }
        return [picUrl, headers];
    },
    range(start, end) {
        return Array(end - start).fill(0).map((el, i) => start + i);
    },
    picDownload(url, path, headers, deciphe) {
        if (typeof deciphe === "string" && deciphe.length > 0) {
            let instream;
            let outstream;
            let file;
            try {
                file = new java.io.File(path);
                if (file.exists()) {
                    return;
                }
                let dir = file.getParentFile();
                if (dir != null && !dir.exists()) {
                    dir.mkdirs();
                }
                instream = fetch(url, {
                    headers: headers,
                    inputStream: true
                });

                outstream = new java.io.FileOutputStream(path);
                let t = $.hiker.input,
                    c = $.hiker.url;
                $.hiker.input = instream;
                $.hiker.url = url;
                let picin = (new Function("return " + deciphe))();
                $.hiker.input = t;
                $.hiker.url = c;
                let buf = java.lang.reflect.Array.newInstance(java.lang.Byte.TYPE, 1024);
                let len;
                while ((len = picin.read(buf)) != -1) {
                    outstream.write(buf, 0, len);
                }
                closeMe(instream);
                closeMe(outstream);
            } catch (e) {
                closeMe(instream);
                closeMe(outstream);
                if (file instanceof java.io.File && file.exists()) {
                    file.delete();
                }
                log("<解密下载Error>" + e.toString())
                throw e;
            } finally {
                closeMe(instream);
                closeMe(outstream);
            }

        } else {
            requireDownload(url, path, headers);
        }
    },
    textShield(text) {
        let e = /#|:|&|\||\^|@|\<|\>|%|\/|\?/g;
        return text.replace(e, "_");
    }

}

$.exports = v;"},{"col_type":"movie_3","name":"#v.主页面","path":"Main.view","rule":"js:
