    for (let i = 0; i < listLength; i++) {
        let item = bufilelist[i];
        let content = request(item.path);
        File.fileWriteAppend(entirePathto, item.name + "\n　　" + content.replace(/&nbsp;/g,"").split(/(\n|\r)+/).filter(it => it.trim().length > 1).map(it=>it.trim()).join("\r　　") + "\n\n");
    }
    if (bookInfo.isReplace) {
        let ruleList = JSON.parse(readFile("replaceRule.json") || "[]");
        let len = ruleList.length;
        let replacePattern = ["replace", "replaceAll"];
        let flag = File.fileRule(entirePathto, entirePath, (javaString) => {
            for (let i = 0; i < len; i++) {
                let replaceRule = ruleList[i];
                if (!bookInfo.selected.includes(replaceRule.id)) {
                    continue;
