    let isCache = decodeURIComponent(getParam("isCache") || "") || MY_PARAMS.isCache;
    if (!path.startsWith("file://")) {
        path = "file://" + path;
    }
    if(MY_PARAMS.title){
        setPageTitle(MY_PARAMS.title);
    }else{
        setPageTitle($.require("hiker://page/File.js").getName(path));
    }
    let layout = [];
    let chapterNames = [];
    let intervals = [];
    let hasPreface = false;
    let cachePath = path.replace(Config.externalImportPath, Config.contentsCache);
    if (isCache&&fileExist(cachePath)) {
        try {

            let c = JSON.parse(request(cachePath));
            chapterNames = c.chapterNames;
            intervals = c.intervals;
            hasPreface = c.hasPreface;
            layout.push({
                title: "重新解析目录",
                col_type: "text_center_1",
                url: $("#noLoading#").lazyRule((path) => {
                    deleteFile(path);
                    refreshPage();
                    return "hiker://empty";
                }, path+".json"),
                extra: {
                    lineVisible:false
                }
            });
            layout.push({
                col_type:"line_blank"
            });
        } catch (e) {
            log(e.toString());
            setResult([{
                title: "““””<strong>目录解析失败(重新解析)</strong>".big(),
                col_type: "text_center_1",
                url: $("#noLoading#").lazyRule((path) => {
                    deleteFile(path);
                    refreshPage();
                    return "hiker://empty";
                }, path+".json"),
                extra: {
                    lineVisible: false
                }
            }]);
            return;
        }

    } else {
        showLoading("正在解析目录");
        let txt = request(path, {
            headers: {
                //"content-type": "text/plain; charst=" + charst
            }
        });
        let pattern = regs.getTocRule(txt);
        if (!pattern) {
            hideLoading();
            setResult([{
                title: "““””<strong>目录解析失败</strong>".big(),
                col_type: "text_center_1",
                url: $("#noLoading#").lazyRule(() => {
                    back(false);
                    return "hiker://empty";
                }),
                extra: {
                    lineVisible: false
                }
            }]);
            return;
        }
        let matcher = pattern.matcher(txt);
        let pointer = 0;
        while (matcher.find()) {
            let start = matcher.start();
            let end = matcher.end();
            let t = matcher.group();
            if (pointer === 0 && start > 30) {
                hasPreface = true;
            }
            chapterNames.push(t);
            intervals.push([pointer, start]);
            pointer = end;
        }
        intervals.push([pointer, txt.length]);
        if (isCache) {
            writeFile(cachePath, JSON.stringify({
                chapterNames: chapterNames,
                intervals: intervals,
                hasPreface: hasPreface
            }));
