                start: interval[0],
                end: interval[1],
                chapterName: name,
                path: path,
                charst: charst
            }
        });
    }
    setResult(layout);
})()"},{"col_type":"movie_3","name":"#v.TXT查看器","path":"TXTViewer.view","rule":"js:
