let Pattern = java.util.regex.Pattern;

function getTocRule(content) {
    let maxCs = 1;
    let tocPattern = null;
    for (let tocRule of rules) {
        if (!tocRule.enable) continue;
        let pattern = Pattern.compile(tocRule.rule, Pattern.MULTILINE);
        let matcher = pattern.matcher(content);
        let cs = 0;
        while (matcher.find()) {
            cs++;
        }
        if (cs >= maxCs) {
            maxCs = cs;
            tocPattern = pattern;
        }
    }
    return tocPattern;
}
