let debug = false;

function clearText(it) {
    return it.replace(/第|集|章/g, "");
}

function isMovie(it) {
    if (it == null || it.text == null) {
        return false;
    }
    it = it.text || "";
    if (it == "" || it.length > 8) {
        return false;
    }
    return it.match(/蓝光|正片|1080P|720P|TC|HD/)
}

function isChapter(it, pre, next) {
    //判断是不是电影
    if (isMovie(it)) {
        return true;
    }
    return isChapter0(it, pre) || isChapter0(it, next);
}

function getChapterNum(it) {
    if (it == null || it.text == null) {
        return -1;
    }
    it = it.text || "";
    if (it == "") {
        return -1;
    }
    it = clearText(it);
    it = parseInt(it);
    if (isNaN(it)) {
        return -1;
    }
    return it;
}

function isChapter0(it, brother) {
    /*if (debug) {
        log({
            it: it,
            brother: brother
        });
    }*/
    it = getChapterNum(it);
    //if (debug) log(it);
    if (it < 0) {
        return false;
    }
    brother = getChapterNum(brother);
    //if (debug) log(brother);
    if (brother < 0) {
        return false;
    }
    return it - brother < 2 && it - brother > -2;
}

