//鲨鱼TV弹幕库|官 
$.exports.shayvdm = function (realurl) {
    filePath = 'hiker://files/cache/danmu/' + 'shayv_from_' + getPageTitle() + '.xml';
    // 字体大小、弹幕步长、解析记录
    var danmuSetting = JSON.parse(readFile('hiker://files/dm盒子/settings.json'));
    var size = danmuSetting.fontSize;
    var step = danmuSetting.step;
    var history = danmuSetting.history;
    if (history['shayv_from_' + getPageTitle()] == realurl && fileExist(filePath)) {
        dm = filePath;
        putVar('dm_share', dm);
        return dm;
    }
    try {
        var dm = "";
        const jiexiPage = "https://www.m4411.com/vip.html?url=" + realurl;
        const html = fetch(jiexiPage);
        const matchedStr = html.match(/var store, [\S\s]+(?=\$\(function\(\))/)[0];
        // console.log(matchedStr);
        const modifyStr = "const store" + matchedStr.match(/=[\S\s]+/)[0]
        const mkSiteInfo = { jxvideoApi: "" };
        eval(modifyStr);
        const videoUrl = "https:" + mkSiteInfo.jxvideoApi + store + '&title=鲨鱼TV:';
        // console.log(videoUrl);
        const videoDate = fetch(videoUrl);
        // console.log(videoDate);
        const danmuAddress = videoDate.match(/addan='(.*?)'/)[1];
        // console.log(addanValue);
        danmu = JSON.parse(fetch(danmuAddress));
        var data = `<d p="0,5,${size},16711680,0">前方共有${danmu.danum / step}条弹幕~</d>\n`;
        danmuData = danmu.data ? danmu.data : danmu.danmuku;
        for (let i = 0; i < danmuData.length; i += step) {
            // 弹幕发送时间
            timepoint = danmuData[i][0];
            // 弹幕样式
            ct = 1;
            if (danmuData[i][1] == 'top') {
                // 顶端弹幕
                ct = 5;
            }
            // 颜色设置
            if (danmuData[i][2] == '#fff') {
                color = 16777215;
            } else {
                color = parseInt(danmuData[i][2].slice(1), 16);
            }
            // 弹幕内容
            content = danmuData[i][4];
            if (content.indexOf('<') != -1 || content.indexOf('>') != -1 || content.indexOf('&') != -1 || content.indexOf('\u0000') != -1 || content.indexOf('\b') != -1) {
                continue;
            }
            tempdata = `<d p="${timepoint},${ct},${size},${color},0">${content}</d>\n`;
            data += tempdata;
        }
        // 最终待写入的弹幕数据
        danmustr = `<?xml version="1.0" encoding="UTF-8"?>\n<i>\n${data}</i>`;
        // 写入弹幕
        dm = 'hiker://files/cache/danmu/' + 'shayv_from_' + getPageTitle() + '.xml';
        saveFile(dm, danmustr);
        history['shayv_from_' + getPageTitle()] = realurl;
        saveFile('hiker://files/dm盒子/settings.json', JSON.stringify(danmuSetting));
        putVar('dm_share', dm);
        return dm;
    } catch (e) {
        log('出错了：' + e)
        showLoading('天呐撸，获取失败了呢！');
        // return 'hiker://files/cache/danmu/' + 'shayv_from_' + getPageTitle() + '.xml';
        return false;
    }
}
