                    options.unshift('YKdm');
                    break;
            }
            return $(options, 1).select((from) => {
                curSettings = JSON.parse(readFile('hiker://files/dm盒子/settings.json'));
                curSettings.dmFrom[from] = input;
                let dmPath = getVar('dm_share', '');
                const filenameWithExt = dmPath.split('/').pop();
                const index = filenameWithExt.lastIndexOf('.');
                const filename = index !== -1 ? filenameWithExt.substring(0, index) : filenameWithExt;
                // 清除上一次解析的记忆
                if (filename) {
