let reaccessTk = getMyVar("aliaccessTk", '');
let timeToken = getMyVar('timeToken', '0');
if (reaccessTk == '' || (parseInt(timeToken) - parseInt(new Date().getTime() / 1000)).toString() <= 600) {
    let ntoken = [];
    let reftoken = "";
    if (!fileExist("hiker://files/rules/icy/icy-ali-token.json")) {
        toast('用户信息获取失败，请登入账号。');
    } else {
        ntoken = JSON.parse(fetch('hiker://files/rules/icy/icy-ali-token.json', {}));
        if (ntoken[0].refresh_token) {
            reftoken = ntoken[0].refresh_token;
        } else {
            toast('用户信息获取失败，请登入账号。');
        }
    }
    if (reftoken.length == 32) {
        var refresh = fetch('https://auth.aliyundrive.com/v2/account/token', {
            headers: {
                'Content-Type': 'application/json',
                'User-Agent': PC_UA,
                'Referer': 'https://www.aliyundrive.com/'
            },
            body: '{"refresh_token":"' + reftoken + '","grant_type":"refresh_token"}',
            method: 'POST'
        });
        ntoken[0] = JSON.parse(refresh);
        writeFile("hiker://files/rules/icy/icy-ali-token.json", JSON.stringify(ntoken));
        log('运行预处理');
        if (!fileExist("hiker://files/rules/icy/icy-ali-token.json") || refresh.length > 300) {
            var refresh = fetch('hiker://files/rules/icy/icy-ali-token.json', {});
            putMyVar('timeToken', (parseInt(JSON.parse(refresh)[0].expires_in) + parseInt(new Date().getTime() / 1000)).toString());
            let defdriveid = JSON.parse(refresh)[0].default_drive_id;
            putMyVar('defdriveid', defdriveid);
            var accessTk = JSON.parse(refresh)[0].token_type + ' ' + JSON.parse(refresh)[0].access_token;
            //log(accessTk);
            putMyVar("aliaccessTk", accessTk);
            let userresinfo = JSON.parse(fetch("https://user.aliyundrive.com/v2/user/get", {
                headers: {
                    'content-type': 'application/json',
                    'User-Agent': PC_UA,
                    "authorization": accessTk,
                    'Referer': 'https://www.aliyundrive.com/'
                },
                body: {},
                method: 'POST'
            }));
            if (userresinfo.resource_drive_id) {
                let resdriveid = userresinfo.resource_drive_id;
                putMyVar('resdriveid', resdriveid);
            }
        }
    }
}

