var s = getResCode();
var json = {};
eval('json=' + s);
var d = [];

for (var i = 0; i < json.Data.data.length; i++) {
    var j = json.Data.data[i];
    var r = {};
    r.desc = j.Desc || j.hotDesc;
    r.title = j.Title.replace(']', '] ').trim();
    r.url = $(j.Url + "#noLoading#").lazyRule(() => {
        const data = fetch(input, {
            headers: {
                Referer: "https://mo.fish/"
            },
            redirect: false,
            withHeaders: true
        });
        const res = JSON.parse(data);
        var url = res.headers.location[0].replace('bbs.tianya.cn//', 'bbs.tianya.cn/m/');
        if (url.indexOf("https://juejin.cnhttps://") >= 0) {
            return "https://" + url.split("//juejin.cnhttps://")[1];
        }
        return url
    });
    r.extra = {
        lineVisible: true
    }
    d.push(r);
}
setResult(d);

