var d = [];
var page = MY_PAGE;
isLogin = JSON.parse($.require("hiker://page/api").isLoggedIn());
// 验证登录
if (isLogin.type === true) {
    biliDate = JSON.parse(request("hiker://files/rules/cy/bili.txt"));
    cookie = biliDate.cookie;
    userName = biliDate.userName;
    mid = biliDate.userMid;
    userFace = biliDate.face + "@Referer=";
    if (page == 1) {
        var Tab = ['热映', '片库', '推送','热门', '订阅', '关注', '收藏', '足迹','动态'];
        Tab = Tab.map((it) => {
            return it === getItem('首页', '推送') ? '👉' + it : it;
        });
        d.push({
            title: '<font color="blue">' + userName,
            desc:'</font>⭐<font color="#19B89D">当前：' + getItem('首页', '推送') + '</font>⭐',
            url: $('#noLoading#').lazyRule((Tab) => {
                return $(Tab, 3).select(() => {
                    input = input.replace(/👉/g, '');
                    setItem('首页', input);
                    refreshPage(true);
                    return "toast://切换首页为:" + input;
                })
            }, Tab),
            img: userFace === undefined ? 'https://lanmeiguojiang.com/tubiao/movie/20.svg' : userFace,
            col_type: "avatar",
            extra: {
                id: '首页显示'
            }
        });
        d.push({
            col_type: 'blank_block'
        })
    }
    // 匹配首页选项
    switch (getItem('首页', '推送')) {
        case '热映':
            eval(JSON.parse(request('hiker://page/UpdateTables')).rule);
            break;
        case '片库':
            eval(JSON.parse(request('hiker://page/vault')).rule);
            break;
        case '推送':
            eval(JSON.parse(request('hiker://page/feed')).rule);
            break;
        case '热门':
            eval(JSON.parse(request('hiker://page/hotVideo')).rule);
            break;
        case '订阅':
            eval(JSON.parse(request('hiker://page/subscription')).rule);
            break;
        case '关注':
            eval(JSON.parse(request('hiker://page/attention')).rule);
            break;
        case '收藏':
            eval(JSON.parse(request('hiker://page/colection')).rule);
            break;
        case '足迹':
            eval(JSON.parse(request('hiker://page/track')).rule);
            break;
        case '动态':
            eval(JSON.parse(request('hiker://page/latetNews')).rule);
            break;
        default:
            eval(JSON.parse(request('hiker://page/UpdateTables')).rule);
            break;
    }
} else {
    let a = []
    let time = 10
    a.push({
        title: `                     使用前须知
本规则调用https://github.com/SocialSisterYi/bilibili-API-collect的api，所有代码全部开源，并且不存在上传用户信息的行为
本规则为学习目的，请于导入24小时内删除！！！                        
开始使用本规则即代表遵守规则                       
            `,
        "col_type": "long_text",
        extra: {
            id: "text"
        }
    }, {
        title: time + "秒后继续",
        col_type: "text_1",
        extra: {
            id: "timer"
        }
    })
    setResult(a)
    while (time != 0) {
        java.lang.Thread.sleep(1000)
        time -= 1
        updateItem("timer", {
            title: time + "秒后继续"
        })
    }
    addItemAfter("timer", {
        title: "登陆",
        url: "hiker://page/login",
        col_type: "text_center_1"
    })
    deleteItem("text")
    deleteItem("timer")
}
