// 登录验证
$.exports.isLoggedIn = function () {
    if (fileExist("hiker://files/rules/cy/bili.txt")) {
        biliDate = JSON.parse(request("hiker://files/rules/cy/bili.txt"));
        var cookie = biliDate.cookie;
        let userinfo = JSON.parse(fetch("https://api.bilibili.com/x/web-interface/nav", {
            headers: {
                "Cookie": cookie
            }
        })).data;
        if (userinfo.isLogin === false) {
            return JSON.stringify({
                "userName": '未登录',
                "userMid": '',
                "userFace": '',
                "type": false
            })
        } else {
            biliDate.userName = userinfo.uname;
            biliDate.userMid = userinfo.mid;
            // 1：会员开通；0：未开通
            biliDate.vipStatus = userinfo.vipStatus;
            biliDate.face = userinfo.face + "@Referer=";
            saveFile('hiker://files/rules/cy/bili.txt', JSON.stringify(biliDate));
            // return true;
            return JSON.stringify({
                "userName": biliDate.userName,
                "userMid": biliDate.userMid,
                "userFace": biliDate.face,
                "type": true
            });
        }
    } else {
        return JSON.stringify({
            "userName": '未登录',
            "userMid": '',
            "userFace": '',
            "type": false
        });
    }
}
// 哔哩视频解析：获取播放地址+提交播放记录
$.exports.biliLazy = function (id, cid, qn) {
    // 重置原生弹幕统一弹幕大小、修改步长
    function overrideSave(dm) {
        var danmuData = request(dm);
        // 字体大小、弹幕步长
        var danmuSetting=JSON.parse(readFile('hiker://files/dm盒子/settings.json'));
        var size = danmuSetting.fontSize;
        var step = danmuSetting.step;
        var data = '';
        td = pdfa(danmuData, 'i&&d');
        for (let i = 0; i < td.length; i+=step) {
            timepoint = pdfh(td[i], 'd&&p').split(",")[0];
            ct = pdfh(td[i], 'd&&p').split(",")[1];
            color = pdfh(td[i], 'd&&p').split(",")[3];
            // 弹幕内容
            content = pdfh(td[i], 'd&&Text');
            if (content.indexOf('<') != -1 || content.indexOf('>') != -1 || content.indexOf('&') != -1 || content.indexOf('\u0000') != -1 || content.indexOf('\b') != -1) {
                continue;
            }
            tempdata = `<d p="${Math.round(timepoint)},${ct},${size},${color},0">${content}</d>\n`;
            data += tempdata;
        }
        danmustr = `<?xml version="1.0" encoding="UTF-8"?>\n<i>\n${data}</i>`;
        saveFile(dm, danmustr);
    }
    // 判断是否重写
    var dm='https://comment.bilibili.com/' + cid + '.xml';
    if (getItem('isOverrideSave','false')=='true') {
        file = 'https://comment.bilibili.com/' + cid + '.xml';
        dm = 'hiker://files/cache/danmu/' + 'bili_from_' + getPageTitle() + '.xml';
        downloadFile(file, dm);
        overrideSave(dm);
    }
    var data = $.require("hiker://page/api").getUrl(id, cid, qn);
    // 提交历史记录
    $.require("hiker://page/api").reportProcess(id, cid);
    // qualityNum=dash.audio.length;不需要了，找到了更好的判断方法，虽然我并不知道这些参数的含义是什么
    videoList = [];
    audioList = [];
    nameList = [];
    headers = [];
    try {
        data.audio.forEach(item => {
            audioList.push(item.baseUrl);
            headers.push({
                "Referer": "https://www.bilibili.com",
                "User-Agent": PC_UA
            });
        });
        data.video.forEach(item => {
            if (item.codecid == 7) {
                videoList.push(item.baseUrl + "#isVideo=true#");
                if (item.id == 126) {
                    nameList.push('杜比视界');
                } else if (item.id == 125) {
                    nameList.push('HDR 真彩色');
                } else if (item.id == 120) {
                    nameList.push('4K超清');
                } else if (item.id == 116) {
                    nameList.push('1080P60高帧率');
                } else if (item.id == 112) {
                    nameList.push('1080P+高码率');
                } else if (item.id == 80) {
                    nameList.push('1080P高清');
                } else if (item.id == 74) {
                    nameList.push('720P60高帧率');
                } else if (item.id == 64) {
                    nameList.push('720P高清');
                } else if (item.id == 32) {
                    nameList.push('480P清晰');
                } else if (item.id == 16) {
                    nameList.push('360P流畅');
                } else if (item.id == 6) {
                    nameList.push('240P极速');
                }
            }
        });
        return JSON.stringify({
            urls: videoList,
            audioUrls: audioList,
            headers: headers,
            names: nameList,
            danmu: dm
        })
    } catch (e) {
        return JSON.stringify({
            urls: [data.durl[0].url + "#isVideo=true#"],
            names: [data.quality + ""],
            danmu: dm,
            headers: [{
                "Referer": "https://www.bilibili.com",
                "User-Agent": PC_UA
            }]
        })
    }
}
