// 操作用户关系
$.exports.changeUserRelation = function (mid, re_src) {
    selection = ['关注', '取关', '悄悄关注', '取消悄悄关注', '拉黑', '取消拉黑', '踢出粉丝', '移动分组'];
    return $(selection, 2).select((mid, re_src) => {
        if (input == '移动分组') {
            beforeTagids = [];
            groups = $.require("hiker://page/api").getUserGroup(mid);
            if (JSON.stringify(groups) == '{}') {
                beforeTagids.push(0);
            } else {
                for (var prop in groups) {
                    beforeTagids.push(parseInt(prop));
                }
            }
            // 获取我的分组
            focusGroupList = [];
            $.require("hiker://page/api").getMyAttentionGroupList().forEach(list => {
                focusGroupList.push(list.name + ' ' + list.tagid);
            });
            return $(focusGroupList, 1).select((beforeTagids, mid) => {
                afterTagids = [];
                afterTagids.push(parseInt(input.split(' ')[1]));
                fids = [];
                fids.push(Number(mid));
                res = $.require("hiker://page/api").moveUsers(beforeTagids, afterTagids, fids);
                if (res == 0) {
                    return "toast://移动用户到" + input.split(' ')[0] + "分组成功"
                } else if (res == -101) {
                    return "toast://账号未登录"
                } else if (res == -111) {
                    return "toast://csrf校验失败"
                } else if (res == -400) {
                    return "toast://请求错误"
                } else if (res == 22104) {
                    return "toast://无权限"
                } else if (res == 22105) {
                    return "toast://未关注"
                } else {
                    return "toast://未知错误"
                }
            }, beforeTagids, mid);
        } else {
            if (input == '关注') {
                var act = 1;
            } else if (input == '取关') {
                var act = 2;
            } else if (input == '悄悄关注') {
                var act = 3;
            } else if (input == '取消悄悄关注') {
                var act = 4;
            } else if (input == '拉黑') {
                var act = 5;
            } else if (input == '取消拉黑') {
                var act = 6;
            } else if (input == '踢出粉丝') {
                var act = 7;
            }
            res = $.require("hiker://page/api").addAttention(mid, act, re_src);
            relation = $.require("hiker://page/api").userMapping(mid);
            updateItem('relation', {
                title: relation,
                img: relation == '未关注' ? storage0.getMyVar('图标').添加关注 : storage0.getMyVar('图标').已关注
            });
            if (res == 0) {
                return "toast://" + input + "成功"
            } else if (res == -101) {
                return "toast://账号未登录"
            } else if (res == -111) {
                return "toast://csrf校验失败"
            } else if (res == -400) {
                return "toast://请求错误"
            } else if (res == 22001) {
                return "toast://不能对自己进行此操作"
            } else if (res == 22003) {
                return "toast://用户位于黑名单"
            } else {
                return "toast://未知错误"
            }
        }
    }, mid, re_src)
}
// 映射用户关系
$.exports.userMapping = function (mid) {
    relationRes = $.require("hiker://page/api").getRelation(mid);
    if (relationRes.relation.attribute == 0) {
        relation = '未关注'
    } else if (relationRes.relation.attribute == 1) {
        relation = '悄悄关注'
    } else if (relationRes.relation.attribute == 2) {
        relation = '已关注'
    } else if (relationRes.relation.attribute == 6) {
        relation = '已互粉'
    } else if (relationRes.relation.attribute == 128) {
        relation = '已拉黑'
    }
    return relation;
}
