// 获取动态数据
$.exports.getDynamicData = function (from, upMid) {
    var nextInfo = {};
    if (MY_PAGE == 1) {
        if (from == 'upSpace') {
            var u = "https://api.bilibili.com/x/polymer/web-dynamic/v1/feed/space?offset=&host_mid=" + upMid + "&timezone_offset=-480";
        } else {
            var u = "https://api.bilibili.com/x/polymer/web-dynamic/v1/feed/all?timezone_offset=-480&type=all";
        }
        latenews = JSON.parse(fetch(u, {
            headers: {
                "Cookie": JSON.parse(request("hiker://files/rules/cy/bili.txt")).cookie,
                "User-Agent": PC_UA,
                "Referer": "https://www.bilibili.com/"
            }
        })).data;
        nextInfo.offset = latenews.offset;
    } else {
        if (from == 'upSpace') {
            var u = "https://api.bilibili.com/x/polymer/web-dynamic/v1/feed/space?offset=" + storage0.getMyVar(JSON.stringify(MY_PAGE)).offset + "&host_mid=" + upMid + "&timezone_offset=-480";
        } else {
            var u = "https://api.bilibili.com/x/polymer/web-dynamic/v1/feed/all?timezone_offset=-480&type=all&offset=" + storage0.getMyVar(JSON.stringify(MY_PAGE)).offset;
        }
        latenews = JSON.parse(fetch(u, {
            headers: {
                "Cookie": JSON.parse(request("hiker://files/rules/cy/bili.txt")).cookie
            }
        })).data;
        nextInfo.offset = latenews.offset;
    }
    storage0.putMyVar(JSON.stringify(MY_PAGE + 1), nextInfo);
    return latenews;
}

