            } else  if (detailFindRule) {
                res.extra = {
                    url: genExUrl(url),
                    RULE: Object.assign(genMyRule(), { detail_find_rule: detailFindRule }),
                    pageTitle: isIndex && title
                }
                res.url = 'hiker://page/frameLoad?rule=' + QING_TITLE
            } else if (MY_RULE.detail_find_rule) {
                res.extra = {
                    url: genExUrl(url),
                    RULE: genMyRule(),
                    pageTitle: isIndex && title
                }
                res.url = 'hiker://page/frameLoad?rule=' + QING_TITLE
            }
        }
        myCollection_el.push(res)
    })
    setResult(myCollection_el)
}
"},{"col_type":"movie_3","name":"数据载入","path":"dataLoad","rule":"$.exports = function(showAll) {
