                const {defaultSets} = $.require("hiker://page/demoData?rule=MyFieldᴰⁿ");
                let parseRoute = 'hiker://files/rules/DuanNian/MyParse.json';
                let jsCode = request(parseRoute)||JSON.stringify({title:[],codes:{},settings:defaultSets});
                try {
                    eval('var json =' + jsCode);
