                let newFile = $.stringify(json); //JSON.parse会报错
                writeFile(parseRoute, newFile);
                refreshPage(false);
                return 'toast://已成功导入'+jiexi.length+'条解析';
            }),
            extra:{
                lineVisible:true
            }
        });
        setResult(d);
    }),
});
d.push({
    title:'☯️查重处理',
    col_type:'scroll_button',
    url:$().lazyRule((unique2,duplicates,floors)=>{
        let parseRoute = 'hiker://files/rules/DuanNian/MyParse.json';
        let jsCode = request(parseRoute);
        try {
            eval('var json =' + jsCode);
        }
        catch (e) {
            return 'toast://本地那个MyParse.json文件有问题,这个功能不给你用了!'
        }
        let titles = json.title;
        let codes = json.codes;
        let keys= Object.keys(codes);
        let keys_re_ct = keys.length - unique2(keys).length; // 名称重复
        let normal_list = [];
        for(let o in codes){
            if(typeof(codes[o])!=='undefined'&&typeof(codes[o])!=='function'){
                normal_list.push(codes[o]);
            }
        }
        let reNorList = unique2(normal_list);
        let value_re_ct = normal_list.length - reNorList.length; //代码重复
        let values_re = duplicates(normal_list);//重复的值
        let ftlist = [];
        let flags = {};
        let myFloors = floors;
        for(let j in codes){//遍历所有
            let one = codes[j];
            try {
                if (typeof(one) !== 'function'&&values_re.includes(one)) {
                    if (typeof (flags[one]) === 'undefined' || !flags[one]) {
                        //取出第一个标志
                        flags[one] = myFloors[0];
                        myFloors.splice(0, 1);//删除标志列表里的第一个元素
                    }
                    ftlist.push({key: flags[one] + j, value: one});
                } else {
                    ftlist.push({key: j, value: one});
                }
            }catch (e) {
                log(e.message)
            }
        }
        // log(JSON.stringify(flags));
        let newCodes = {};
        for(let k in ftlist){
            newCodes[ftlist[k].key] = ftlist[k].value;
        }
        let tips = '检测到名称复用项目数:'+keys_re_ct+',代码重复项目数量:'+value_re_ct+'\n需要一键打个标识吗?不支持自动删除,打标志自行解决';
        return $(tips).confirm((value_re_ct,json,newCodes,parseRoute)=>{
            if(value_re_ct<1){
                return 'toast://既然都是0个重复项目了，就不需要我做什么了!'
            }
            function compare(){
                return function (a, b){
                    const {floors} = $.require("hiker://page/demoData?rule=MyFieldᴰⁿ");
                    let x = a.substring(0,1);
                    let y = b.substring(0,1);
                    return floors.indexOf(y)-floors.indexOf(x);
                }
            }
            json.codes = newCodes;
            let titles = Object.keys(newCodes);
            titles = titles.sort(compare());
            json.title = titles;
            var newFile = $.stringify(json); //JSON.parse会报错
            writeFile(parseRoute, newFile);
            refreshPage(false);
            return 'toast://已一键打上标志';
        },value_re_ct,json,newCodes,parseRoute)
    },unique2,duplicates,floors),
});
