        let parseUrlText = request("hiker://files/jiexi/jiexi.txt");
        let parseUrlArray = parseUrlText.split(/\n/);
        if(input.split("&&").length!==2){
            return "toast://格式不正确";
        }
        parseUrlArray.push(input);
        parseUrlText = parseUrlArray.join("\n");
        writeFile("hiker://files/jiexi/jiexi.txt", parseUrlText);

        refreshPage(false);
        return "toast://修改成功";
    }),
    col_type: "scroll_button"
});
for (let item of classTab) {
    d.push({
        title: item[1] === classA ? "““" + item[0] + "””" : item[0],
        url: $("#noLoading#").lazyRule((key) => {
            putVar("class", key);
            refreshPage(false);
            return "hiker://empty";
        }, item[1]),
        col_type: "scroll_button"
    });
}
let func;
switch (classA) {
    case "0":
        func = function(url, title) {
            return $(url + "#noLoading#").lazyRule((title) => {
                log("\n更改接口> " + input);
                setItem("selected", input);
                setItem("selected0", title + "&&" + input);
                refreshPage(false);
                return "toast://修改成功";
            }, title);
        };
        break;
    case "1":
        func = function(url, title, index) {
            return $(url, "请输入「" + title + "」的新接口").input((title, index) => {
                let parseUrlText = request("hiker://files/jiexi/jiexi.txt");
                let parseUrlArray = parseUrlText.split(/\n/);
                let selected1 = parseUrlArray[index];
                parseUrlArray[index] = title + "&&" + input;
                parseUrlText = parseUrlArray.join("\n");
                writeFile("hiker://files/jiexi/jiexi.txt", parseUrlText);
                if (selected1 == getItem("selected0")) {
                    setItem("selected", input);
                    setItem("selected0", title + "&&" + input);
                }
                refreshPage(false);
                return "toast://修改成功";
            }, title, index);
        };
        break;
    case "2":
        func = function(url, title, index) {
            return $("确认删除「" + title + "」？").confirm((title, index) => {
                let parseUrlText = request("hiker://files/jiexi/jiexi.txt");
                let parseUrlArray = parseUrlText.split(/\n/);
                let selected1 = parseUrlArray[index];
                parseUrlArray.splice(index, 1);
                parseUrlText = parseUrlArray.join("\n");
                writeFile("hiker://files/jiexi/jiexi.txt", parseUrlText);
                if (selected1 == getItem("selected0")) {
                    if (parseUrlArray[0]) {
                        setItem("selected", parseUrlArray[0].split("&&")[1] || "");
                        setItem("selected0", parseUrlArray[0]);
                    } else {
                        setItem("selected", "");
                        setItem("selected0", "");
                    }
                }
                refreshPage(false);
                return "toast://修改成功";
            }, title, index);
        };
        break;

}
for (let i in parseUrlArray) {
    let item = parseUrlArray[i].split("&&");
