function sortByKey(array, key, order) {//对象数组按某个键值排序
    return array.sort(function(a, b) {
        var x = parseInt(a[key].match(/(\d+)/)[1]);//修复大于10出问题
        var y = parseInt(b[key].match(/(\d+)/)[1]);
        if (order) {
            return ((x < y) ? -1 : ((x > y) ? 1 : 0))
        } else {
            return ((x < y) ? ((x > y) ? 1 : 0) : -1)
        }
    })
}
