            check, test
        } = $.require("hiker://page/yzm");
        if (check(url, html, d)) {
            log("重试了");
            html = request(url, {
                headers: {
                    "Referer": url
                }
            });           
        }         
        test(url, html, d, old, key, rule);
    } else {
        //自动识别post请求
        html = request(url);
        let form = pdfh(html, "body&&form&&Html");
        let action = pd(html, "form&&action", url);
        let inputs = pdfa(html, "body&&form&&input");
        let body = {};
        for (let it of inputs) {
            let name = pdfh(it, "input&&name");
            if (name && name != "") {
                let value = pdfh(it, "input&&value");
                body[name] = value || key;
            }
        }
        html = post(action, {
            body: body
        });
    }

    let alist = pdfa(html, "body&&a");
    let arr = alist.map(it => {
        let t = pdfh(it, "a&&Text");
        let t2 = pdfh(it, "a&&title");
        return {
            //html: it,
            text: t2 && t2.includes(key) ? t2 : t,
            href: pd(it, "a&&href", url)
        }
    });

    function isResult(it) {
        it = it.text || "";
        if (it == "" || it.match(/搜索|结果/)) {
            return false
        }
        return it.toLowerCase().includes(key);
    }
    key = key.toLowerCase();
    for (let i = 0; i < arr.length; i++) {
        let it = arr[i];
        let t = it.text;
        if (!it.href || it.href == "") {
            continue;
        }
        if (isResult(it)) {
